package com.ashu.tutorials;

public class BasicParameters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println("Hello World!!!");
       byte myByte = 64;
       int  myInt = 34456;
       short myShort = (short)345;
       float myFloat = 34.7879F;
       double myDouble = 234.34534667;
       char myChar='\u00E9';
       boolean myBoolean = false;
       String myString="This is also a String";
       
       System.out.println("Byte : " +myByte);
       System.out.println("Integer : " +myInt +"\nShort : "+myShort);
       System.out.println("Float : " +myFloat +"\nDouble : "+myDouble);
       System.out.println("Unicode Char : " +myChar + "\nBoolean, is Male? "+myBoolean);
       System.out.println("String : " +myString);
       
	}

}
